package ru.jmentor.rest;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import ru.jmentor.model.User;import ru.jmentor.service.UserService;@RestController@RequestMapping("/rest/register")public class RegisterRestController {    private UserService service;    @Autowired    public void setService(UserService service) { this.service = service; }    @PostMapping    public void createUser(@RequestBody User user) { service.register(user); }}